import { useState } from 'react';

function UserForm() {
  const [name, setName] = useState('');
  const [age, setAge] = useState('');

  return (
    <div>
      <p>Name: {name}</p>
      <p>Age: {age}</p>
    </div>
  );
}

export default UserForm;
