task.solution:
function CustomButton({ text, variant = 'primary', onClick }) {
  const variants = {
    primary: 'bg-blue-600 hover:bg-blue-700 text-white',
    secondary: 'bg-gray-600 hover:bg-gray-700 text-white',
    danger: 'bg-red-600 hover:bg-red-700 text-white'
  };

  return (
    <button
      onClick={onClick}
      className={`px-6 py-3 rounded-lg font-medium ${variants[variant] || variants.primary}`}
    >
      {text}
    </button>
  );
}

function App() {
  return (
    <div className="p-8 space-x-4">
      <CustomButton text="Save" variant="primary" onClick={() => alert('Saved!')} />
      <CustomButton text="Cancel" variant="secondary" onClick={() => alert('Cancelled')} />
      <CustomButton text="Delete" variant="danger" onClick={() => alert('Deleted!')} />
    </div>
  );
}

export default App;

