import { useState } from 'react';

function PatientStatus() {
  const [status, setStatus] = useState('Stable');

  return (
    <div className="p-6 bg-white rounded shadow">
      <h2 className="text-xl font-bold mb-4">Patient Status</h2>

      <p className="mb-4">Current: {status}</p>

      {status === 'Critical' && (
        <p className="text-red-600 font-bold">ğŸš¨ Immediate Attention Required</p>
      )}

      {status !== 'Critical' && (
        <p className="text-green-600">âœ… No emergency</p>
      )}

      <div className="mt-4 space-x-2">
        <button onClick={() => setStatus('Stable')}>Stable</button>
        <button onClick={() => setStatus('Critical')}>Critical</button>
      </div>
    </div>
  );
}

export default PatientStatus;

