task.solution:
import { useState } from 'react';

function BedStatus() {
  const [beds, setBeds] = useState(1);

  return (
    <div className="p-6">
      {beds > 0 ? (
        <p className="text-green-600">Beds Available</p>
      ) : (
        <p className="text-red-600">Hospital Full</p>
      )}

      <button onClick={() => setBeds(beds - 1)}>Admit Patient</button>
    </div>
  );
}

export default BedStatus;

