challenge.solution:
function Header() {
  return <h1>React Gym</h1>;
}

function Content() {
  return <p>Learn React step by step</p>;
}

function Footer() {
  return <p>All rights reserved</p>;
}

function App() {
  return (
    <div>
      <Header />
      <Content />
      <Footer />
    </div>
  );
}

export default App;
