task.solution:
import { BrowserRouter, Routes, Route, Link, useParams } from 'react-router-dom';

function UserDetail() {
  const { id } = useParams();
  const names = { '1': 'Alex', '2': 'Jordan', '3': 'Taylor' };

  return (
    <div className="p-12 text-center">
      <h1 className="text-5xl font-bold mb-4">User Profile</h1>
      <p className="text-2xl">ID: {id}</p>
      <p className="text-3xl mt-4">Name: {names[id] || 'Unknown'}</p>
    </div>
  );
}

function App() {
  return (
    <BrowserRouter>
      <nav className="p-6 bg-gradient-to-r from-purple-600 to-indigo-600 text-white shadow-lg">
        <div className="max-w-4xl mx-auto flex gap-8 justify-center">
          <Link to="/users/1" className="px-6 py-3 bg-white/20 rounded hover:bg-white/30">User 1 - Alex</Link>
          <Link to="/users/2" className="px-6 py-3 bg-white/20 rounded hover:bg-white/30">User 2 - Jordan</Link>
          <Link to="/users/3" className="px-6 py-3 bg-white/20 rounded hover:bg-white/30">User 3 - Taylor</Link>
        </div>
      </nav>
      <Routes>
        <Route path="/users/:id" element={<UserDetail />} />
      </Routes>
    </BrowserRouter>
  );
}

export default App;

