function TaskItem({ task, toggle, remove }) {
  return (
    <li>
      <span
        style={{ textDecoration: task.completed ? 'line-through' : 'none' }}
        onClick={() => toggle(task.id)}
      >
        {task.text}
      </span>
      <button onClick={() => remove(task.id)}>X</button>
    </li>
  );
}

export default TaskItem;

